import React from "react";
import { FaCheck, FaThumbsDown, FaThumbsUp } from "react-icons/fa";
import WatchBtn from "../WatchBtn";

export default function MovieInfo({ movie }) {

  const genres = [
    { "id": 10759, "name": "Action & Adventure" }, { "id": 16, "name": "Animação" }, { "id": 35, "name": "Comédia" }, { "id": 80, "name": "Crime" }, { "id": 99, "name": "Documentário" }, { "id": 18, "name": "Drama" }, { "id": 10751, "name": "Família" }, { "id": 10762, "name": "Kids" }, { "id": 9648, "name": "Mistério" }, { "id": 10763, "name": "News" }, { "id": 10764, "name": "Reality" }, { "id": 10765, "name": "Sci-Fi & Fantasy" }, { "id": 10766, "name": "Soap" }, { "id": 10767, "name": "Talk" }, { "id": 10768, "name": "War & Politics" }, { "id": 37, "name": "Faroeste" }, { "id": 28, "name": "Ação" }, { "id": 12, "name": "Aventura" }, { "id": 16, "name": "Animação" }, { "id": 35, "name": "Comédia" }, { "id": 80, "name": "Crime" }, { "id": 99, "name": "Documentário" }, { "id": 18, "name": "Drama" }, { "id": 10751, "name": "Família" }, { "id": 14, "name": "Fantasia" }, { "id": 36, "name": "História" }, { "id": 27, "name": "Terror" }, { "id": 10402, "name": "Música" }, { "id": 9648, "name": "Mistério" }, { "id": 10749, "name": "Romance" }, { "id": 878, "name": "Ficção científica" }, { "id": 10770, "name": "Cinema TV" }, { "id": 53, "name": "Thriller" }, { "id": 10752, "name": "Guerra" }, { "id": 37, "name": "Faroeste" }
  ]

  let firstDate = new Date(movie.first_air_date);

  const showGenre = (genreID) => {
    const genreShow = [];
    genres.forEach(item => {
      if(item.id === genreID) {
        console.log(item.name)
        return genreShow.push(item.name)
      }
    })
    return genreShow
  }
  const genresMovie = [];
  for (let i in movie.genre_ids) {
    genresMovie.push(movie.genre_ids[i]);
  }

  const finalGenres = []
  function writeGenre(){
    const genres = [];
    genresMovie.map((item)=> genres.push(showGenre(item)))
   return console.log(genres)
  }

  return (
    <article className="movieInfo">
      <div className="movieInfo__preview">
        <div className="movieInfo__preview__panel">
          <h2 className="movielInfo__title">{movie.name}</h2>
          <div className="movieInfo__btns">
            <WatchBtn />
            <div className="movieInfo__btn">
              <FaCheck />
            </div>
            <div className="movieInfo__btn">
              <FaThumbsUp />
            </div>
            <div className="movieInfo__btn">
              <FaThumbsDown />
            </div>
          </div>
        </div>
      </div>
      <div className="movieInfo__close">
      </div>
      <div className="movieInfo__about">
        <div className="movieInfo__content">
          <div className="movieInfo__bar">
            <span className="movieInfo__points">{movie.vote_average}</span>
            <span className="movieInfo__date">{firstDate.getFullYear()}</span>
          </div>
          <p className="movieInfo__description">
            {movie.overview}
          </p>
        </div>
        <div className="movieInfo__other">
          <h3 className="movieInfo__other__title">Títulos semelhantes</h3>
          <span>Gêneros: {writeGenre}</span>
          <div className="movieInfo__other__movies">

          </div>
        </div>
      </div>
    </article>
  )
}
