import { FaPlusCircle } from 'react-icons/fa';
import AccountsCard from '../../components/AccountsCard';
import Logo from '../../components/Logo';

export default function ManageProfile() {

  // &__avatar#user1 {
  //   background-image: url('../imgs/users/user1.png');
  // }
  // &__avatar#user2 {
  //   background-image: url('../imgs/users/user4.png');
  // }
  // &__avatar#user3 {
  //   background-image: url('../imgs/users/user9.png');
  // }
  // &__avatar#user4 {
  //   background-image: url('../imgs/users/user7.png');
  // }
  const users = [
    { name: 'Fábio', icon: 'url(../../imgs/users/user1.png)', link: '/home' },
    { name: 'Felipe', icon: '../../imgs/users/user4.png', link: '/home' },
    { name: 'Silvana', icon: '../../imgs/users/user9.png', link: '/home' },
    { name: 'Isabella', icon: '../../imgs/users/user7.png', link: '/home' },
  ]

  return (
    <>
      <section className='accounts'>
        <header className='accounts__header'>
          <Logo />
        </header>
        <main className='accounts__container'>
          <div className='accounts__content'>
            <h2 className='accounts__title'>Quem está assistindo?</h2>
            <ul className='accounts__list'>
              {users.map((item) => (
                <AccountsCard
                  link={item.link}
                  name={item.name}
                  icon={item.icon}
                />
              ))}
              <AccountsCard
                link="/home"
                name="Adicionar perfil"
                bg="5"
                customClass='prof--ad'
              >
                <FaPlusCircle />
              </AccountsCard>
            </ul>
            <div className='accounts__edit'>
              <a href="#!">Gerenciar perfis</a>
            </div>
          </div>
        </main>
      </section>
    </>
  )
}
