import { useState } from "react";
import './Categorias.css'
export default function Categorias({ lista }) {

  const [itemAtivo, setItemAtivo] = useState(0);
  const item = document.querySelectorAll('.categorias__link[id]');
  
  function linkAtivo(id){
    e.preventDefault();
    document.querySelectorAll(`.categorias__link[${id}]`).classList.add('active')
  }
  return (
    <nav className="categorias_container">
      <ul className="categorias__lista flex flex_ai_c flex_jc_sb" data-lista>
        {lista.map((item, id) => (
          <li className="categorias__item" key={id}>
            <a href={item.href} className="categorias__link" id={id} onClick={(e)=>linkAtivo(e)}>{item.nome}</a>
          </li>
        ))}
      </ul>
    </nav>
  )
}
