import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import Container from "../../auxiliar/Container";
import Logo from "../../components/Logo";
import Form from "../../components/Form";
import Footer from "../../layout/Footer";
import MyFooter from "../../layout/MyFooter";
import InputForm from "../../components/InputForm";

export default function Login() {

  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [clients, setClients] = useState([]);

  const [errorMessage, setErrorMessage] = useState()
  const navigate = useNavigate()

  useEffect(() => {
    fetch(`http://localhost:5000/clients`, {
      method: "GET",
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((resp) => resp.json())
      .then((data) => {
        setClients(data)
      })
      .catch(err => {
        console.log(err)
      })
  }, [])

  function handleEmailChange(e) {
    var inputEmail = e.target.value;
    setEmail(inputEmail);
  }
  function handleSenhaChange(e) {
    var inputSenha = e.target.value;
    setSenha(inputSenha);
  }

  const errorMessages = {
    missingData: "Você não preencheu todos os campos. Tente novamente!",
    emailNotFound: "Desculpe, não encontramos uma conta com esse endereço de email. Tente novamente ou <a href='#'>crie um nova conta</a>.",
    wrongPassword: `<strong>Senha incorreta</strong>. Tente novamente!`
  }

  function authClient(email, password) {
    let message = ''
    clients.forEach(item => {

      if ((item.email === email) && (item.password === password)) {
        setErrorMessage(false)
        return navigate("/")
      }
      if ((email === '') || (password === '')) {
        setErrorMessage('missingData')
      }
      if ((item.email !== email) && (email !== '')) {
        setErrorMessage('emailNotFound')
      }
      if ((item.email === email) && (item.password !== password)) {
        message = `<span`
        setErrorMessage('wrongPassword')
      }
    })
  }
  return (
    <>
      <header className="login__header">

        <Container customClass={'login__header__container'}>
          <div className="login__logo">
            <Logo />
          </div>
        </Container>

      </header>
      <main>
        <div className="login__bg">
          <div className="login__form">
            <Form title="Entrar" onClick={() => authClient(email, senha)}>
              <div className="login__errors">
                {errorMessage &&
                  <span className="login__error">{errorMessages[errorMessage].toString()}</span>
                }
              </div>
              <div className="login__inputs">
                <InputForm
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={email}
                  onChange={handleEmailChange}
                />
                <InputForm
                  type="password"
                  name="password"
                  placeholder="Senha"
                  value={senha}
                  onChange={handleSenhaChange}
                />
              </div>
            </Form>
          </div>
        </div>
      </main>
      <Footer />
      <MyFooter />
    </>
  )
}
