import React, { useEffect, useState } from "react";
import Tmdb from "./Tmdb.js";
import FeatureMovie from "./components/FeatureMovie.js";
import MovieRow from "./components/MovieRow.js";
import Header from "./components/Header.js";
import "./Styles/main.css";
export default function App() {

  const [movieList, setMovieList] = useState([]);
  const [featureData, setFeatureData] = useState(null);


  //Pegando items
  function pegaItems(movieList) {
    const validados = [];
    movieList.forEach((item) => {
      if (item.items.post_path !== null) {
        validados.push(item)
      }
    })
    return validados;
  }

  useEffect(() => {
    const loadAll = async () => {
      // Pegando a lista Total
      let list = await Tmdb.getHomeList();
      console.log(list)
      setMovieList(list);

      //Pegando o feature
      let originals = list.filter(i => i.slug === 'originals');
      let randomChosen = Math.floor(Math.random() * (originals[0].items.results.length - 1));
      let chosen = originals[0].items.results[randomChosen];
      let chosenInfo = await Tmdb.getMovieInfo(chosen.id, 'tv')
      setFeatureData(chosenInfo);
    }

    loadAll();
  }, []);
  return (
    <div className="page">
      <Header />

      {featureData &&
        <FeatureMovie
          item={featureData}
        />
      }

      <section className="lists">
        {movieList.map((item, key) => (
          <MovieRow
            key={key}
            title={item.title}
            items={pegaItems(movieList)}
          />
        ))}
      </section>
      <footer className="footer">
        <p>Feito com dedicação por Fábio Signorini</p>
        <p>Direitos de imagem para Netflix</p>
        <p>Dados utilizados do site Themoviedb.org</p>
      </footer>
    </div>
  );
}
