import React, { useState, useEffect } from 'react';
import Container from '../../../auxiliar/Container';
export default function SearchPage({ searchChange, movieList, setMovieInfo }) {

  const [searchValue, setSearchValue] = useState();

  useEffect(() => {
    setSearchValue(searchChange)
  });

  return (
    <section className='searchPage'>
      <Container>
        <h2 className="searchPage__title">Procurar por: "{searchValue}"</h2>
        <div className='searcPage__results'>
          {movieList.map((item) => {
            console.log(item)
            item.items.results.filter((value) => {
              console.log(searchValue.toLowerCase())
              if(searchValue === null) {
                return value
              } else if (value.name.toLowerCase().includes(searchValue.toLowerCase())) {
                return value
              }
            }).map((item, key) => (
              <li>{item.name}</li>
            ))
          })}
        </div>
      </Container>
    </section>
  )
}
