import { useState } from "react";

export default function Categorias({ lista }) {
  const [itemAtivo, setItemAtivo] = useState(0);
  function linkAtivo(id){
    const item = document.querySelectorAll('[categorias__link]');
    item[itemAtivo].classList.remove('active')
    setItemAtivo(id)
    item[itemAtivo].classList.add('active')
  }
  return (
    <nav className="categorias_container">
      <ul className="categorias__lista flex flex_ai_c flex_jc_sb" data-lista>
        {lista.map((item, key) => (
          <li className="categorias__item" key={key}>
            <a href={item.href} className="categorias__link" onClick={()=>linkAtivo(key)}>{item.nome}</a>
          </li>
        ))}
      </ul>
    </nav>
  )
}
