import React from "react";
import '../Styles/Header.css';
import logo from '../imgs/logo.png';
import user from '../imgs/user.png';
import {FaSearch, FaBell} from 'react-icons/fa';
export default function Header() {

  function scrollBg(){
    const header = document.querySelector('.header');
    
    if (window.scrollY > 30) {
      header.style.background = 'linear-gradient(to bottom, rgb(0,0,0), rgb(20,20,20))';
      header.style.transition 
    } else {
      setTimeout(()=> {
        header.style.background = 'linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(20,20,20,0))';
      },3000)
    }
  }
  window.addEventListener('scroll', scrollBg)
  return (
    <header className="header" data-header>
      <nav className="menu">
        <a href="/" className="menu__logo">
          <img src={logo} alt="Netflix Home" />
        </a>
        <ul className="menu__list">
          <li className="menu__item">
            <a href="/" className="menu__link active">In√≠cio</a>
          </li>
          <li className="menu__item">
            <a href="/" className="menu__link">S√©ries</a>
          </li>
          <li className="menu__item">
            <a href="/" className="menu__link">Filmes</a>
          </li>
          <li className="menu__item">
            <a href="/" className="menu__link">Bombando</a>
          </li>
          <li className="menu__item">
            <a href="/" className="menu__link">Minha lista</a>
          </li>
        </ul>
      </nav>
      <div className="menu__user">
        <div className="menu__user__search">
          <FaSearch />
        </div>
        <div className="menu__user__kids">
          <FaBell />
        </div>
        <div className="menu__user__notification"></div>
        <a href="/" className="menu__user__logo">
          <img src={user} alt="Netflix User" />
        </a>
      </div>
    </header>
  )
}
