    }
  }

  const errorMessages = {

  }
  return (
    <>
      <header className="login__header">

        <Container customClass={'login__header__container'}>
          <div className="login__logo">
            <Logo />
          </div>
        </Container>

      </header>
      <main>
        <div className="login__bg">
          <div className="login__form">
            <Form title="Entrar">
              <div className="form__inputs">
                <InputForm
                  type="text"
                  name="email"
                  placeholder="Email ou NÃºmero de Telefone"
                  value={email}
                  onChange={handleEmailChange}
                />
                <InputForm
                  type="password"
                  name="password"
                  placeholder="Senha"
                  value={senha}
                  onChange={handleSenhaChange}
                />
                <div className="form__error"></div>
              </div>
              <div className="form__btn">
                <button type="button" className="linkBtn linkBtn--main" onClick={() => authClient(email, senha)}>{title}</button>
              </div>
            </Form>
          </div>
        </div>
      </main>
      <Footer />
      <MyFooter />
    </>
  )
}
