import React, {useState, useEffect} from 'react';
import FeatureMovie from "../../components/FeatureMovie";
import MovieRow from "../../components/MovieRow";
import Loading from "../../layout/Loading";
import Header from "../../layout/Header";
import MobileHeaderBottom from '../../layout/MobileHeader/MobileHeaderBottom';
export default function Home({ windowWidth, featureData, movieList }) {

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {

      await new Promise((r) => setTimeout(r, 500));
      setLoading((loading) => !loading);
    };
      
    loadData();
  }, [])

  const users = [
    { name: 'Fábio', icon: 'user1', link: '/home' },
    { name: 'Felipe', icon: 'user4', link: '/home' },
    { name: 'Silvana', icon: 'user9', link: '/home' },
    { name: 'Isabella', icon: 'user7', link: '/home' },
  ]

  function toggleAccount(e){
    const imgAccount = e.target.id;
    console.log(imgAccount)
  }
    
  if (loading) {
      return <Loading />
  }

  else {
  return (
    <>
      <Header windowWidth={windowWidth} movieList={movieList}/>
      {featureData &&
        <FeatureMovie
          item={featureData}
          windowWidth={windowWidth}
        />
      }

      <section className="lists">
        {movieList.map((item, key) => (
          <MovieRow
            key={key}
            title={item.title}
            items={item.items}
          />
        ))}
      </section>
      <footer className="footer">
        <p>Feito com dedicação por Fábio Signorini</p>
        <p>Direitos de imagem para Netflix</p>
        <p>Dados utilizados do site Themoviedb.org</p>
      </footer>

      {movieList.length <= 0 &&
        <Loading />
      }
      {windowWidth < 1200 &&
        <MobileHeaderBottom/>
      }
    </>
  )
}
}
