import React from "react";
import { FaCheck, FaThumbsDown, FaThumbsUp } from "react-icons/fa";
import WatchBtn from "../WatchBtn";

export default function MovieInfo({ movie }) {

  let firstDate = new Date(movie.first_air_date);


  const API_KEY = '02029514bf460de87d0f37958f707b0c';
  const API_BASE = 'https://api.themoviedb.org/3';

  const basicFetch = async (endpoint) => {
    const req = await fetch(`${API_BASE}${endpoint}`);
    const json = await req.json();
    return json;
  }

  const genresName = [];
  basicFetch(`/genre/tv/list?api_key=${API_KEY}&language=pt-BR`)
    .then(resp => {
      return genresName.push(resp.genres)
    })
  basicFetch(`/genre/movie/list?api_key=${API_KEY}&language=pt-BR`)
    .then(resp => {
      return genresName.push(resp.genres)
    })

