import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Container from "../../auxiliar/Container";
import Logo from "../../components/Logo";
import Form from "../../components/Form";
import Footer from "../../layout/Footer";
import MyFooter from "../../layout/MyFooter";

import LinkBtn from "../../components/LinkBtn";
import ConfigCard from "../../pages/NewClient/ConfigCard";
import CreatePassword from "../../pages/NewClient/CreatePassword";

export default function NewClient() {
  const initialInputValues = {
    email: '',
    password: ''
  }
  const [inputValues, setInputValues] = useState(initialInputValues)
  
  function handleChange(e){
    setInputValues({
      ...inputValues,
      [e.target.name]: e.target.value
    })
  }

  const navigate = useNavigate()

  const initialCardPages = {
    configCard: true,
    passwordCard: false,
  }
  const [cardPages, setCardPages] = useState(initialCardPages)

  function changeCard(){
    setCardPages({
      passwordCard: true,
      configCard: false
    })
  }

  return (
    <>
      <header className="newClient__header">

        <Container customClass={'newClient__header__container flex flex_ai_c flex_jc_sb'}>
          <div className="newClient__logo">
            <Logo />
          </div>
          <LinkBtn to="/login" title="Entrar" customClass="linkBtn--main" />
        </Container>

      </header>

      <main>

          <div className="newClient__bg flex flex_ai_c">
            <ConfigCard />
          </div>
          <div className="newClient__bg flex flex_ai_c">
            <CreatePassword
              valueEmail={inputValues.email}
              valuepassword={inputValues.password}
            />
          </div>
      </main>
      <Footer />
      <MyFooter />
    </>
  )
}
