import React, { useState, useEffect } from 'react';
import Container from '../../../auxiliar/Container';
export default function SearchPage({ searchChange, movieList, setMovieInfo }) {

  const [searchValue, setSearchValue] = useState("A");

  // useEffect(() => {
  //   setSearchValue(searchChange)
  // });

  return (
    <section className='searchPage'>
      <Container>
        <h2 className="searchPage__title">Procurar por: "{searchValue}"</h2>
        <div className='searcPage__results'>
          {movieList.map((item) => {
            item.items.results.filter((value) => {
              const valueItem = '';
              if(searchValue === undefined ) {
                return value
              } else if (value.name) {
                if (value.name.toLowerCase().includes(searchValue.toLowerCase())) {
                  return value
                }
              } else if (value.title) {
                if (value.title.toLowerCase().includes(searchValue.toLowerCase())) {
                  return value
                }
              }
            }).map((item, key) => (
              <li key={key}>{item.name}</li>
            ))
          })}
        </div>
      </Container>
    </section>
  )
}
