class AuthService {

  signIn(){
    fetch(`http://localhost:5000/clients`, {
      method: "GET",
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((resp) => resp.json())
      .then((data) => {
        return data
      })
      .catch(err => {
        console.log(err)
      })     
  }

  newCliente(email, password){
    fetch(`http://localhost:3000/db/clients`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        email: email,
        password: password 
      })
    })
  }
}
const authService = new AuthService();

export default authService;
