        items: await basicFetch(`/movie/top_rated?language=pt-BR&api_key=${API_KEY}`)
      },      
      {
        slug: 'popular',
        title: 'Populares',
        items: await basicFetch(`/tv/popular?language=pt-BR&api_key=${API_KEY}`)
      },      
      {
        slug: 'action',
        title: 'Ação',
        items: await basicFetch(`/discover/movie?with_genres=28&language=pt-BR&api_key=${API_KEY}`)
      },      
      {
        slug: 'comedy',
        title: 'Comédia',
        items: await basicFetch(`/discover/movie?with_genres=35&language=pt-BR&api_key=${API_KEY}`)
      },      
      {
        slug: 'horror',
        title: 'Terror',
        items: await basicFetch(`/discover/movie?with_genres=27&language=pt-BR&api_key=${API_KEY}`)
      },      
      {
        slug: 'romance',
        title: 'Romance',
        items: await basicFetch(`/discover/movie?with_genres=10749&language=pt-BR&api_key=${API_KEY}`)
      },      
      {
        slug: 'documentary',
        title: 'Documentários',
        items: await basicFetch(`/discover/movie?with_genres=99&language=pt-BR&api_key=${API_KEY}`)
      }
    ];
  },
  getMovieInfo: async (movieId, type) => {
    let info = {};

    if(movieId){
      switch(type){
        case 'movie':
          info = await basicFetch(`/movie/${movieId}?language=pt-BR&api_key=${API_KEY}`);
        break;
        case 'tv': 
          info = await basicFetch(`/tv/${movieId}?language=pt-BR&api_key=${API_KEY}`);
        break;
        default:
          info = null;
        break;
      }
    }
    return info;
  },
  getSeriesList: async () => {
    return [
      {
        slug: 'originals',
        title: 'Originais do Netflix',
        items: await basicFetch(`/discover/tv?with_networks=213&language=pt-BR&api_key=${API_KEY}`)
      },
      {
        slug: 'action',
        title: 'Ação e Aventura',
        items: await basicFetch(`/discover/tv?api_key=${API_KEY}&sort_by=popularity.desc&with_genres=10759`)
      },
      {
        slug: 'animated',
        title: 'Animação',
        items: await basicFetch(`discover/tv?api_key=${API_KEY}&sort_by=popularity.desc&with_genres=16`)
      },
      {
        slug: 'comedy',
        title: 'Comédia',
        items: await basicFetch(`discover/tv?api_key=${API_KEY}&sort_by=popularity.desc&with_genres=35`)
      },
      {
        slug: 'crime',
        title: 'Crime',
        items: await basicFetch(`discover/tv?api_key=${API_KEY}&sort_by=popularity.desc&with_genres=80`)
      },
      {
        slug: 'drama',
        title: 'Drama',
