import React from "react";
import Tmdb from "../../Tmdb";
import './MobileHeader.css';
import logo from '../../imgs/logo_mobile.png';
import user from '../../imgs/user.png';
import { FaSearch, FaTv } from 'react-icons/fa';
import Categorias from "./Categorias";

export default function MobileHeader() {

  function scrollBg() {
    const header = document.querySelector('.mb_header');

    if (window.scrollY > 30) {
      header.style.background = 'rgba(0,0,0,0.9)';
    } else {
      header.style.background = 'linear-gradient(to bottom, rgba(0,0,0,0.6) 30%, rgba(20,20,20,0))';
    }
  }

  const lista = [
    {}
  ]

  window.addEventListener('scroll', scrollBg)
  return (
    <header className="mb_header flex flex_ai_c flex_jc_sb" data-header>
      <div className="mb_header__bar">
        <a href="/" className="mb_header__logo">
          <img src={logo} alt="Netflix Home" />
        </a>
        <div className="mb_header__icons flex flex_ai_c">
          <a href="/" className="mb_header__tv">
            <FaTv />
          </a>
          <a href="/" className="mb_header__search">
            <FaSearch />
          </a>
          <a href="/" className="mb_header__profile">
            <img src={user} alt="Netflix User" />
          </a>
        </div>
      </div>
      <nav className="mb_menu">
        <ul className="mb_menu__list">
          <Categorias
            lista={[{nome:'Series', href:'/'},{nome:'Series', href:'/'},{nome:'Series', href:'/'}]}/>
        </ul>
      </nav>

    </header>
  )
}
