import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import Container from "../../auxiliar/Container";
import Logo from "../../components/Logo";
import Form from "../../components/Form";
import Footer from "../../layout/Footer";
import MyFooter from "../../layout/MyFooter";
import InputForm from "../../components/InputForm";
import Button from "../../components/Button";
import LinkBtn from "../../components/LinkBtn";
import newClientImg from '../../imgs/pages/newClient/Devices.png'

export default function NewClient() {

  const [values, setValues] = useState('')
  
  function handleChange(e){
    const fieldName = e.target.getAttribute('name');
    const { value } = e.target;
    setValues({
      ...values,
      [fieldName]: value
    })
  }
  const navigate = useNavigate()

  const [configCard, setConfigCard] = useState(true)
  const [passwordCard, setPasswordCard] = useState(false)

  function changeCard() {
    setConfigCard(false)
    setPasswordCard(true)
  }

  return (
    <>
      <header className="newClient__header">

        <Container customClass={'newClient__header__container flex flex_ai_c flex_jc_sb'}>
          <div className="newClient__logo">
            <Logo />
          </div>
          <LinkBtn to="/login" title="Entrar" customClass="linkBtn--main" />
        </Container>

      </header>

      <main>
        {configCard &&
          <div className="newClient__bg flex flex_ai_c">
            <div className="newClient__card flex flex_ai_c">
              <img src={newClientImg} alt="" className="newClient__img" />
              <span className="newClient__step">Passo <strong>1</strong> de <strong>3</strong></span>
              <h2 className="newClient__title">Conclua a configuração da sua conta</h2>
              <div className="newClient__text">
                <span>Netflix é personalizado para você.</span>
                <span>Crie uma senha para assistir em qualquer dispositivo a qualquer hora.</span>
              </div>
              <div className="newClient__btn">
                <Button title="Next" onClick={changeCard} />
              </div>
            </div>
          </div>
        }
        {passwordCard &&
          <div className="newClient__bg flex flex_ai_c">
            <div className="newClient__card flex flex_ai_c">
              <span className="newClient__step">Passo <strong>1</strong> de <strong>3</strong></span>
              <h2 className="newClient__title">Crie uma senha para começar sua conta</h2>
              <div className="newClient__text">
                <span>Apenas mais alguns passos e finalizamos!</span>
                <span>Nós também odiamos papelada.</span>
              </div>
              <form className="newClient__form">
                <InputForm
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={email}
                />
                <InputForm
                  type="password"
                  name="password"
                  placeholder="Senha"
                  value={senha}
                  pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?!.*[!@#$%^_=+-]).{6,12}$"
                />
              </form>
              <div className="newClient__btn">
                <Button title="Next" onClick={changeCard} />
              </div>
            </div>
          </div>
        }
      </main>
      <Footer />
      <MyFooter />
    </>
  )
}
