import React, { useEffect, useState } from "react";
import Tmdb from "./Tmdb.js";
import FeatureMovie from "./components/FeatureMovie";
import MovieRow from "./components/MovieRow";
import MainHeader from "./components/MainHeader";
import MobileHeader from "./components/MobileHeader";
import netflixLoading from '../src/imgs/netflix-loading.gif';
import "./Styles/main.css";

export default function App() {
  const [movieList, setMovieList] = useState([]);
  const [featureData, setFeatureData] = useState(null);

  useEffect(() => {
    const loadAll = async () => {
      // Pegando a lista Total
      let list = await Tmdb.getHomeList();
      setMovieList(list);

      //Pegando o feature
      let originals = list.filter(i => i.slug === 'originals');
      let randomChosen = Math.floor(Math.random() * (originals[0].items.results.length - 1));
      let chosen = originals[0].items.results[randomChosen];
      let chosenInfo = await Tmdb.getMovieInfo(chosen.id, 'tv')
      setFeatureData(chosenInfo);
    }

    loadAll();
  }, []);


  /*const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  useEffect(() => {
    function handleResize() {
      setWindowWidth(window.innerWidth)
    }
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  },[]);*/

  return (
    <div className="page">
      {window.innerHeight < 1200 ? <MobileHeader categorias={movieList}/> : <MainHeader />}

      {featureData &&
        <FeatureMovie
          item={featureData}
          windowWidth={windowWidth}
        />
      }

      <section className="lists">
        {movieList.map((item, key) => (
          <MovieRow
            key={key}
            title={item.title}
            items={item.items}
          />
        ))}
      </section>
      <footer className="footer">
        <p>Feito com dedicação por Fábio Signorini</p>
        <p>Direitos de imagem para Netflix</p>
        <p>Dados utilizados do site Themoviedb.org</p>
      </footer>

      {movieList.length <= 0 &&
        <div className="loading">
          <img src={netflixLoading} alt="Carregando.." />
        </div>
      }
    </div>
  );
}
