import React, { useState, useEffect } from 'react';
import FeatureMovie from "../../components/FeatureMovie";
import MovieRow from "../../components/MovieRow";
import Loading from "../../layout/Loading";
import Header from "../../layout/Header";
import MobileHeaderBottom from '../../layout/MobileHeader/MobileHeaderBottom';
import MyFooter from '../../layout/MyFooter';
import MovieInfo from '../../components/MovieInfo';
import MovieLists from './MovieLists';
export default function Home({ windowWidth, featureData, movieList, loggedUser, users, children }) {

  const [loading, setLoading] = useState(true);
  const [movieInfo, setMovieInfo] = useState('')
  useEffect(() => {
    const loadData = async () => {

      await new Promise((r) => setTimeout(r, 500));
      setLoading((loading) => !loading);
    };
    loadData();
  }, [])



  if (loading) {
    return <Loading />
  }

  else {
    function openMoreInfo(item) {
      setMovieInfo(item)
      const movieInfo = document.querySelector('.movieInfo')
      movieInfo.classList.add('open')
    }
    return (
      <>
        <Header windowWidth={windowWidth} movieList={movieList} loggedUser={loggedUser} users={users} />
        {featureData &&
          <FeatureMovie
            item={featureData}
            windowWidth={windowWidth}
          />
        }
        {children}
        
        {children !== true &&
          <MovieLists
            movieList={movieList}
            movieInfo={setMovieInfo}
            openMoreInfo={openMoreInfo}
          />
        }
        {movieInfo &&
          <MovieInfo movie={movieInfo} />
        }
        <MyFooter />
        {movieList.length <= 0 &&
          <Loading />
        }
        {windowWidth < 1200 &&
          <MobileHeaderBottom />
        }
      </>
    )
  }
}
