import React, { useState, useEffect } from "react";
import LinkBtn from "../../components/LinkBtn";
import { FaFacebookF } from 'react-icons/fa';
import { useNavigate } from "react-router-dom";

export default function Form({ title }) {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [clients, setClients] = useState([]);
  const [correctUser, setCorrectUser] = useState(false);

  const navigate = useNavigate()

  function handleEmailChange(e) {
    var inputEmail = e.target.value;
    setEmail(inputEmail);
  }
  function handleSenhaChange(e) {
    var inputSenha = e.target.value;
    setSenha(inputSenha);
  }

  useEffect(() => {
    fetch(`http://localhost:5000/clients`, {
      method: "GET",
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((resp) => resp.json())
      .then((data) => {
        setClients(data)
      })
      .catch(err => {
        console.log(err)
      })
  }, []);

  function handleSignIn(email, password) {
    clients.forEach(item => {
      if((item.email === email) && (item.password === password)){
        setCorrectUser(true)
        navigate("/")
      }
    })
  }

  return (
    <div className="form">
      <h2 className="form__title">{title}</h2>
      <form action="" className="form__container">
        <div className="form__inputs">
          <div className="form__item">
            <input type="text" className="form__input" name="form__input" placeholder="Email ou Número de Telefone"
              value={email}
              onChange={handleEmailChange}
            />
            <label htmlFor="form__input" className="form__label">Email ou Número de Telefone</label>
            <span className="form__error">Informe um email ou número de telefone válido.</span>
          </div>
          <div className="form__item">
            <input type="password" className="form__input" name="form__input" placeholder="Senha" data-senha
              value={senha}
              onChange={handleSenhaChange}
            />
            <label htmlFor="form__input" className="form__label">Senha</label>
            <span className="form__error">A senha deve ter entre 4 e 60 caracteres.</span>
          </div>
        </div>
        <div className="form__btn">
          <button type="button" className="linkBtn linkBtn--main" onClick={() => handleSignIn(email, senha)}>Entrar</button>
        </div>
        <div className="form__help flex flex_ai_c flex_jc_sb">
          <div className="form__rememberMe flex flex_ai_c">
            <input type="checkbox" name="remeber_me" defaultChecked />
            <label htmlFor="">Lembre-se de mim</label>
          </div>
          <div className="form__helpMe">
            <a href="#!">Precisa de ajuda?</a>
          </div>
        </div>
      </form>
      <div className="form__other">
        <a href="#!" className="form__social flex flex_ai_c">
          <FaFacebookF className="form__social__icon" />
          <span>Conectar com Facebook</span>
        </a>
        <div className="form__subscribe">
          <span>Novo por aqui? <a href="#!">Assine agora!</a></span>
        </div>
        <div className="form__reCaptcha">
          <span>Esta página é protegida pelo Google reCAPTCHA para garantir que você não é um robô. <a href="#!">Saiba mais.</a></span>
        </div>
      </div>
    </div>
  )
}
