import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import Container from "../../auxiliar/Container";
import Logo from "../../components/Logo";
import Form from "../../components/Form";
import Footer from "../../layout/Footer";
import MyFooter from "../../layout/MyFooter";
import InputForm from "../../components/InputForm";

export default function Login() {

  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const [clients, setClients] = useState([]);

  //VALIDATE
  const [correctUser, setCorrectUser] = useState(false);
  const [falseEmailMessage, setFalseEmailMessage] = useState(false);

  const navigate = useNavigate()

  useEffect(() => {
    fetch(`http://localhost:5000/clients`, {
      method: "GET",
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((resp) => resp.json())
      .then((data) => {
        setClients(data)
      })
      .catch(err => {
        console.log(err)
      })
  }, [])

  function handleEmailChange(e) {
    var inputEmail = e.target.value;
    setEmail(inputEmail);
  }
  function handleSenhaChange(e) {
    var inputSenha = e.target.value;
    setSenha(inputSenha);
  }


  function authClient(email, password) {
    clients.forEach(item => {
      if ((item.email === email) && (item.password === password)) {
        setCorrectUser(true)
        return navigate("/")
      } else if (item.email !== email) {
        setFalseEmailMessage(true)
      } else if ((item.email === email) && (item.password !== password)) {
        setFalseEmailMessage(false)
      } else {
        setCorrectUser(false)
      }
    })
  }
  
  const itemError = document.querySelector('.login__error_password');

  if(correctUser !== true) {
    itemError.classList.add('error');
  } else {
    itemError.classList.remove('error');
  }

  return (
    <>
      <header className="login__header">

        <Container customClass={'login__header__container'}>
          <div className="login__logo">
            <Logo />
          </div>
        </Container>

      </header>
      <main>
        <div className="login__bg">
          <div className="login__form">
            <Form title="Entrar" onClick={() => authClient(email, senha)}>
              <span className="login__error" id="loginError">Senha incorreta. Tente novamente!</span>
              {falseEmailMessage === true &&
                <span className="login__error">Desculpe, não encontramos uma conta com esse endereço de email. Tente novamente ou <Link to="newAccount" className="login__error__redirect">crie um nova conta.</Link></span>
              }
              {falseEmailMessage === false &&
                <span className="login__error">Senha incorreta. Tente novamente!</span>
              }
              <div className="login__inputs">
                <InputForm
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={email}
                  onChange={handleEmailChange}
                />
                <InputForm
                  type="password"
                  name="password"
                  placeholder="Senha"
                  value={senha}
                  onChange={handleSenhaChange}
                />
              </div>
            </Form>
          </div>
        </div>
      </main>
      <Footer />
      <MyFooter />
    </>
  )
}
