import React from 'react'

export default function MenuList({ data }) {

  function activeSection(e){
    const items = document.querySelectorAll('.menuList__link')
    e.target.classList.add('active')
  }

  return (
    <ul className="menuList">
      {data.map((item) => (
        <li className="menuList__item">
          <a href={item.link} className="menuList__link" onClick={activeSection}>{item.title}</a>
        </li>
      ))}
    </ul>
  )
}
