import React from "react";
import { FaHome, FaGamepad, FaPlaneArrival, FaSmileBeam, FaArrowAltCircleDown } from 'react-icons/fa'
import { Link } from "react-router-dom";
import './MobileHeaderBottom.css'

export default function MobileHeaderBottom() {

  const menuItems = [
    {name: 'Início', icon: 'FaHome', href: '/home'},
    {name: 'Jogos', icon: 'FaGamepad', href: '/home'},
    {name: 'Em breve', icon: 'FaPlaneArrival', href: '/home'},
    {name: 'Risadas', icon: 'FaSmileBeam', href: '/home'},
    {name: 'Download', icon: 'FaArrowAltCircleDown', href: '/home'},
  ]

  // function linkAtivo(e, id) {
  //   item.forEach(item => {
  //     item.classList.remove('active')
  //   })
  //   e.preventDefault()
  //   item[id + 1].classList.add('active')
  // }

  return (
    <div className="mhb__container flex flex_ai_c ">
      <ul className="mhb__items flex flex_ai_c flex_jc_sb">
        {
          menuItems.map()
        }
        <li className="mhb__item active" onClick={(e)=>paginaAtiva(e)}>
          <Link to="/home" className="mhb__link flex ">
            <FaHome className="mhb__icon" />
            <span className="mhb__title">Início</span>
          </Link>
        </li>
        <li className="mhb__item" onClick={(e)=>paginaAtiva(e)}>
          <Link to="/home" className="mhb__link flex">
            <FaGamepad className="mhb__icon" />
            <span className="mhb__title">Jogos</span>
          </Link>
        </li>
        <li className="mhb__item" onClick={(e)=>paginaAtiva(e)}>
          <Link to="/home" className="mhb__link flex">
            <FaPlaneArrival className="mhb__icon" />
            <span className="mhb__title">Em breve</span>
          </Link>
        </li>
        <li className="mhb__item" onClick={(e)=>paginaAtiva(e)}>
          <Link to="/home" className="mhb__link flex">
            <FaSmileBeam className="mhb__icon" />
            <span className="mhb__title">Risadas</span>
          </Link>
        </li>
        <li className="mhb__item" onClick={(e)=>paginaAtiva(e)}>
          <Link to="/home" className="mhb__link flex">
            <FaArrowAltCircleDown className="mhb__icon" />
            <span className="mhb__title">Download</span>
          </Link>
        </li>

      </ul>
    </div>
  )
}
