import React, { useState } from "react";
import { FaCaretDown } from "react-icons/fa";
import FeatureMovie from "../../../components/FeatureMovie";
import MovieRow from "../../../components/MovieRow";

export default function Browse({ featureData, movieList, openMoreInfo, title }) {

  const [showGenres, setShowGenres] = useState(false);

  function toggleGenres(){
    setShowGenres(!showGenres)
  }

  return (
    <>
      {title &&
      <div className="browse__details">
        <h2 className="browse__title">{title}</h2>
        <div className="browse__genres" onClick={toggleGenres}>
          <span className="browse__genres__item">GÃªneros</span>
          <FaCaretDown />
        </div>
        {showGenres &&
          <div className="browse__dropdown">
            <ul className="browse__list">
              {movieList.map((item, key) => (
                <li></li>
              ))}
            </ul>
          </div>
        }
      </div>
      }
      {featureData &&
        <FeatureMovie
          item={featureData}
        />
      }
      <section className={`lists ${featureData ? '' : 'lists--ajust'}`}>
        {movieList.map((item, key) => (
          <MovieRow
            key={key}
            title={item.title}
            items={item.items}
            onClickItem={openMoreInfo}
          />
        ))}
      </section>
    </>
  )
}
