import React from "react";
import { FaHome, FaGamepad, FaPlaneArrival, FaSmileBeam, FaArrowAltCircleDown } from 'react-icons/fa'
import { Link } from "react-router-dom";
import './MobileHeaderBottom.css'

export default function MobileHeaderBottom() {

  const menuItems = [
    { name: 'Início', icon: <FaHome className="mhb__icon" />, href: '/home' },
    { name: 'Jogos', icon: <FaGamepad className="mhb__icon" />, href: '/home' },
    { name: 'Em breve', icon: '<FaPlaneArrival className="mhb__icon" />', href: '/home' },
    { name: 'Risadas', icon: '<FaSmileBeam className="mhb__icon" />', href: '/home' },
    { name: 'Download', icon: '<FaArrowAltCircleDown className="mhb__icon" />', href: '/home' },
  ]

  // function linkAtivo(e, id) {
  //   item.forEach(item => {
  //     item.classList.remove('active')
  //   })
  //   e.preventDefault()
  //   item[id + 1].classList.add('active')
  // }

  return (
    <div className="mhb__container flex flex_ai_c ">
      <ul className="mhb__items flex flex_ai_c flex_jc_sb">
        {
          menuItems.map((item) => (
            <li className="mhb__item" onClick="">
              <Link to={item.href} className="mhb__link flex ">
                {item.icon}
                <span className="mhb__title">{item.name}</span>
              </Link>
            </li>
          ))
        }
      </ul>
    </div>
  )
}
