import React from "react";
import { FaCheck, FaThumbsDown, FaThumbsUp } from "react-icons/fa";
import WatchBtn from "../WatchBtn";

export default function MovieInfo({ movie }) {

  let firstDate = new Date(movie.first_air_date);
  let genres = [];

  const API_KEY = '02029514bf460de87d0f37958f707b0c';
  const API_BASE = 'https://api.themoviedb.org/3';

  const basicFetch = async (endpoint) => {
    const req = await fetch(`${API_BASE}${endpoint}`);
    const json = await req.json();
    return json;
  }
  function getGenres() {
    basicFetch
  }

  return (
    <article className="movieInfo">
      <div className="movieInfo__preview">
        <div className="movieInfo__preview__panel">
          <h2 className="movielInfo__title">{movie.name}</h2>
          <div className="movieInfo__btns">
            <WatchBtn />
            <div className="movieInfo__btn">
              <FaCheck />
            </div>
            <div className="movieInfo__btn">
              <FaThumbsUp />
            </div>
            <div className="movieInfo__btn">
              <FaThumbsDown />
            </div>
          </div>
        </div>
      </div>
      <div className="movieInfo__close">
      </div>
      <div className="movieInfo__about">
        <div className="movieInfo__content">
          <div className="movieInfo__bar">
            <span className="movieInfo__points">{movie.vote_average}</span>
            <span className="movieInfo__date">{firstDate.getFullYear()}</span>
          </div>
          <p className="movieInfo__description">
            {movie.overview}
          </p>
        </div>
        <div className="movieInfo__details">
          <div className="movieInfo__tags">
            <span className="movieInfo__tags__title">Gêneros:{genres.join(',')}</span>

          </div>
        </div>
      </div>
    </article>
  )
}
